/* ==UserStyle==
@name           Octopi-B
@namespace      https://github.com/Catgrills/Octopi-B
@version        1.0.0
@description    An userstyle for AnimeBytes called Octopi-B.
@author         Catgrills/Katgrills
@preprocessor uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #F0F1F5 !important; 
		--card-bg: #FFF !important;
		--card-bg-2: #F9F9F9 !important;
		--card-bg-3: #F0F0F0 !important;
		--border-color: #D5D5D5 !important;
		--primary-color: #212121 !important;
		--secondary-color: #7E7E7E !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #162126 !important; 
		--card-bg: #282933 !important;
		--card-bg-2: #24242B !important;
		--card-bg-3: #242930 !important;
		--border-color: #000 !important;
		--primary-color: #FFF !important;
		--secondary-color: #EBEBEB !important;
	}
	`
}
==/UserStyle== */

@-moz-document domain("animebytes.tv") {
/*[[theme]]*/
/* GENERAL */

    /** DESCRIPTION : set the main font and change others elements **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
    }

    *::-moz-focus-inner {
        border: 0 !important;
    }

    :focus { 
        outline: none !important; 
    }

    select {
        background: var(--card-bg);
        border: 1px solid var(--border-color) !important;
        color: var(--primary-color);
        -webkit-appearance: none;
        -moz-appearance: none;
        text-indent: 0px;
        text-overflow: ellipsis;
        white-space: nowrap;
    }

    html {
        overflow-x: hidden !important;
    }

    body,
    #content {
        background: var(--body-bg) !important;
    }

    a {
        color: #5F5AA2;
    }

    a:hover {
        color: #8A8ABE;
    }

    table a {
        color: #5F5AA2 !important;        
    }

    hr {
        background: var(--card-bg);
        border-color: var(--border-color) !important;
    }

    .box {
        background: var(--card-bg-2) !important;
        border-color: var(--border-color) !important;
        border-radius: 1px !important;
    }

    #content {
        width: 90% !important;
        max-width: 100% !important;
    }

    #torrents #wrapper,
    #torrents2 #wrapper{
        margin-bottom: 80px;
    }

    #wrapper #content .thin,
    #wrapper #content .thin {
        margin-right: 45px !important;
    }

    .page-link,
    .next-prev {

        /*** PAGINATIONS ***/
        background: var(--card-bg);
        border: 1px solid var(--border-color) !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
    }

    .nolink {
        font-weight: normal !important;
    }

    textarea,
    #desc_edit input[type="text"] {

        /*** TEXTAREA ***/
        background: var(--card-bg-3) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--primary-color);
        font-size: 15pt !important;
        font-stretch: condensed;
        max-width: 90% !important;
        padding: 15px !important;
        margin-bottom: 10px !important;
    }

    #smileys span {
        margin-right: -3px !important;
        margin-bottom: -6px !important;
    }

    input[name="subject"],
    input {
        background: var(--card-bg-3);
        border: 1px solid var(--border-color);
        color: var(--primary-color);
        font-stretch: condensed;
        max-width: 100% !important;
    }

    .sidebar form input {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 5px 0px;
        margin-top: 10px !important;
        margin-bottom: 10px !important;
    }

    .btn-sub[type="submit"],
    .btn-sub[type="button"],
    .btn-sub,
    #navi_desciption a,
    .nav_tags.ui-tabs-anchor,
    #messageform input,
    #quickreplybuttons input,
    input[value="Add"],
    input[value="Add poll!"],
    input[value="Autofill"],
    input[value="Autofill!"],
    input[value="Clear"],
    input[value="Clear Default"],
    input[value="Already said THANKS!"],
    input[value="Click to say THANKS!"],
    input[value="Create"],
    input[value="Delete"],
    input[value="Edit collage"],
    input[value="Grab the pack"],
    input[value="Fill request"],
    input[value="Filter Screenshots"],
    input[value="Make Default"],
    input[value="Manage conversation"],
    input[value="Preview"],
    input[value="Reset"],
    input[value="Search"],
    input[value="Search requests"],
    input[value="Send Yen!"],
    input[value="Submit Checked Torrents"],
    input[value="Submit report"],
    input[value="Update"],
    input[value="Upload"],
    input[value="Upload torrent"],
    input[value="Upload!"],
    input[value="Vote"] {

        /*** BUTTONS ***/
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        padding: 8px 15px !important;
        margin-right: 5px !important;
        text-transform: uppercase !important;
    }

    input[value="+"] {
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        width: 35px !important;
    }

    .nav_tags.ui-tabs-anchor {
        padding-top: 11px !important;
        padding-bottom: 10px !important;
    }

    input[value="Already said THANKS!"],
    input[value="Clear"],
    input[value="Clear Default"],
    input[value="Click to say THANKS!"],
    input[value="Grab the pack"],
    input[value="Send Yen!"],
    input[value="Submit Checked Torrents"],
    input[value="Submit report"],
    input[value="Vote"] {
        background: #FCB941 !important;
    }

    #extended_btn,
    #messageform input,
    #navi_desciption a,
    .nav_tags.ui-tabs-anchor,
    input[value="Autofill"],
    input[value="Autofill!"],
    input[value="Delete"],
    input[value="Fill request"],
    input[value="Manage conversation"],
    input[value="Reset"],
    input[value="Search requests"] {
        background: #60646D !important;
    }

    #navi_desciption a {
        height: 25px !important;
        line-height: 25px !important;
    }

    #preview_button,
    input[value="+"],
    input[value="Add"],
    input[value="Add poll!"],
    input[value="Create"],
    input[value="Edit collage"],
    input[value="Filter Screenshots"],
    input[value="Make Default"],
    input[value="Preview"],
    input[value="Update"],
    input[value="Upload"],
    input[value="Upload torrent"],
    input[value="Upload!"] {
        background: #2CC990 !important;
    }

    input[value="Search"] {
        background: #3D8EB9 !important;
    }

    .message,
    .friend_request {
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        padding: 10px 20px 12px 20px !important;
        position: fixed !important;
        bottom: 10px !important;
        right: 20px !important;
    }

    .message {
        background: #263D4E !important;        
    }

    .friend_request {
        background: #6AB04C !important;
    }

    .profile_comment {
        background: #686DE0 !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 15px 20px !important;
        position: fixed !important;
        right: 20px !important;
        bottom: 10px !important;
    }

    .friend_request a,
    .message * {
        color: #FFF !important;
    }

    .alert-success {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .save_message,
    #achievementBar {

        /*** SAVE MESSAGES ***/
        background: #263D4E !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        height: 30px !important;
        line-height: 27px !important;
        position: fixed !important;
        bottom: 0px !important;
        left: 20px !important;
    }

    .yui-navset p[style="text-align: center;"],
    .yui-navset p[style="text-align: center;"] * {

        /*** PERSONAL ANNOUNCE URL ***/
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
        line-height: 22pt !important;
    }

    #achievementBar,
    #browse_nav_right,
    #footer,
    #forums .linkbox a[href*="action=viewunread"],
    .main_column > br,
    #show_hide_tags,
    #rutCarousel,
    .tags a[href="torrents.php?tags="],
    .tags a[href="torrents2.php?tags="] {
        display: none !important;
    }

    #forums #wrapper .thin .box:not(.pad) {
        display: none;
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    #header {

        /*** HEADER ***/ 
        background: #323442 !important;
        height: 0px !important;
        padding: 30px !important;
    }

    #header #logo {

        /*** HEADER LOGO ***/
        top: 20px !important;
        left: 40px !important;
        margin: 0 !important;
        z-index: 99 !important;
    }

    #header #userinfo {

        /*** HEADER USER BAR ***/
        width: 80% !important;
        position: absolute !important;
        top: 10px !important;
        right: 100px !important;
        z-index: 9999 !important;
    }

    #userinfo .navmenu.selected {
        background: transparent !important;
    }

    #header #userinfo .subnav {
        background: #282933 !important;
        border: 1px solid #000 !important;
        border-radius: 1px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        padding: 15px !important;
        margin-top: 10px !important;
    }

    #header #userinfo .subnav li {
        padding: 5px !important;
    }

    #header #userinfo,
    #header #username_menu {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        line-height: 17px !important;
    }

    #header a[href*="inbox"]::before {
        content: "Inbox" !important;
        width: 100px !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
    }

    #header a[href*="inbox"] {
        margin-right: 5px;
    }

    #header a[href*="inbox"] img {
        display: none;
    }

    .r99 {
        font-size: 14pt !important;
    }

    #yen_count {
        font-family: "Roboto", "KFhimaji";
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
    }

    #header #userinfo .dropit {
        margin-right: 10px !important;
        margin-top: 2px !important;
    }

    #menu {

        /*** HEADER MENU ***/
        background: #2C5784;
        padding: 22px 0px 38px 0px !important;
        width: 100% !important;
        position: absolute !important;
        top: 60px !important;
        left: 0px !important;
        z-index: 999 !important;
    }

    #header #menu ul {
        position: absolute !important;
        left: 40px !important;
    }

    #menu * {
        font-size: 12pt !important;
    }

    #menu .main-menu li,
    #searchboxes-btn,
    #advancesearch-btn {
        border-radius: 1px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        font-stretch: condensed !important;
        font-weight: bold;
        letter-spacing: .5px !important;
        line-height: 24px;
        padding: 6px 0px !important;
        text-transform: uppercase !important;
        margin-right: 8px !important;
    }

    #searchboxes-btn {
        background: #313E50;
        border: none;
        color: #FFF;
        height: 37px !important;
        width: 150px !important;
        position: absolute;
        right: 95px !important;
    }

    #advancesearch-btn {
        border-radius: 1px !important;
        background: #5688C7;
        border: none;
        color: #FFF;
        height: 37px !important;
        width: 180px !important;
        position: absolute;
        right: 253px !important;
    }

    #menu .main-menu li {
        background: #282933;
    }

    #menu .main-menu li:hover {
        border-radius: 1px !important;
        background: #313E50 !important;
        color: #FFF !important;
    }

    #menu .main-menu li a {
        color: #FFF;
    }

    #menu .main-menu li:hover a {
        color: #FFF !important;
    }

    #menu .main-menu li .dropit {
        height: 9px;
        width: 11px;
        margin-top: -5px !important;
        margin-right: 20px !important;
    }

    #menu span.dropit.clickmenu:hover,
    #userinfo span.dropit.clickmenu:hover {
        background-color: transparent !important;
        filter: invert(1) !important;
    }

    .dropit.hover.clickmenu {
        padding-bottom: 2px !important;
    }

    #menu .main-menu .subnav {
        background: #313E50 !important;
        border: none !important;
        border-radius: 3px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding-top: 10px;
        padding-bottom: 10px;
        margin-top: 20px !important;
        margin-left: -41px !important;
    }

    #menu ul.subnav li {
        background: #313E50 !important;
        border-bottom: 1px dotted #BEBEBE !important;
        box-shadow: none !important;
        color: #FFF !important;
        font-size: 16pt !important;
        font-family: "Roboto" !important;
        font-weight: bold !important;
        padding-top: 8px !important;
        padding-bottom: 10px !important;
        text-transform: uppercase !important;
    }

    #menu ul.subnav li:first-child {
        padding-top: 0px !important;
    }

    #menu ul.subnav li:last-child {
        border: none !important;
        padding-bottom: 0px !important;
    }

    #menu ul.subnav li:hover,
    #menu ul.subnav li a:hover {
        background: transparent !important;
        color: #7E7E7E !important;
    }

    #menu ul.subnav li * {
        color: #FFF !important;
        font-family: "Roboto" !important;
    }

    #browse_nav_sections {

        /*** HEADER NAVIGATION BAR ***/
        background: var(--card-bg) !important;
        border-top: 1px solid var(--border-color) !important;
        padding: 16px 0px;
        text-align: center !important;
        width: 100% !important;
        position: fixed !important;
        left: 0px !important;
        bottom: -101px;
        float: none !important;
        z-index: 9999 !important;
        transition: all .3s ease-in-out !important;
    }

    #browse_nav_top {
        width: 100% !important;
    }

    #browse_nav_top h2 {
        color: transparent;
        font-size: 17pt !important;
        font-stretch: condensed !important;
        font-weight: normal;
        text-transform: capitalize !important;
        margin-bottom: 15px !important;
    }

    #browse_nav_sections #categories {
        border-right: none !important;
        text-align: center !important;
    }

    #browse_nav_sections #categories li {
        font-size: 15pt;
        font-stretch: condensed;
        display: inline-block !important;
        margin-right: 10px !important;
        margin-left: 10px !important;
    }

    #browse_search {

        /*** HEADER ADVANCED SEARCH ***/
        width: 95% !important;
        margin-top: 50px;
        display: none;
    }

    input[type="checkbox"],
    .center input[type="checkbox"] { 
        margin-top: 8px !important;
        margin-right: 6px !important;
    }

    #filter input[type="checkbox"]{
        margin-top: 8px !important;
        margin-right: 5px !important;
        margin-left: 6px !important;
    }

    input[name="ispoll"], 
    input[name="tags_type"],
    .ct_not_guest {
        margin-top: 8px !important;
    }

    #screenshots #nav_torrents,
    #torrents #nav_torrents,
    #torrents2 #nav_torrents,
    #forums #nav_forums {

        /*** TORRENTS MENU BUTTON ***/
        border-radius: 1px !important;
    }

    #searchbars {
        background: var(--card-bg);
        border-bottom: 1px solid var(--border-color);
        padding: 11px 11px 6px 11px !important;
        margin-top: 0px;
        margin-bottom: -40px !important;
    }

    #searchbars ul {
        background: transparent !important;
        margin-top: -10px !important;
    }

    #searchbars input {
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 2px !important;
        color: var(--primary-color);
        font-size: 15pt !important;
        font-stretch: condensed !important;
        padding: 7px 10px 8px 10px !important;
        text-transform: lowercase !important;
        margin-right: 8px;
    }

    #konbini .quicksearch.series_search {
        width: 150px !important;
    }

    /* HOME */

    /** DESCRIPTION : change the elements of the home page **/
    .fpleft {
        width: 79% !important;
    }

    .news_entry {

        /*** HOME NEWS ***/
        color: var(--primary-color);
        font-size: 16pt !important;
        font-stretch: condensed !important;
        line-height: 19pt !important;
    }

    .news_entry {
        border-bottom: 1px dotted #BEBEBE !important;
    }

    #news .news_entry > div[style="text-align: center;"] {

        /*** HOME NEWS IMAGES ***/
        text-align: left !important;
        margin-bottom: 0px !important;
    }

    #news .news_entry > div[style="text-align: center;"] * {
        font-weight: normal;
    }

    .spoilerContainer.hideContainer > input[type="button"],
    .spoilerContainer input[type="button"] {
        background: #1F9EA3 !important;
        border-radius: 2px !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        min-width: 180px !important;
        text-transform: uppercase !important;
    }

    .spoiler {
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--primary-color) !important;
        padding: 15px !important;
        margin-top: 10px !important;
    }

    .codeBox pre {
        background: var(--card-bg-3) !important;
        border: 1px solid var(--border-color) !important;        
    }

    .past_news.nobullet {

        /*** HOME PAST NEWS ***/
        padding-bottom: 10px !important;
        margin-left: -9px !important;
    }

    .past_news.nobullet li {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--primary-color);
        padding: 0px 20px 20px 20px !important;
        margin-top: 20px !important;
        margin-bottom: -10px !important;
    }

    .past_news.nobullet .title {
        font-size: 18pt !important;
    }

    .past_news.nobullet .meta,
    .past_news.nobullet .meta * {
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-style: normal !important;
        margin-top: 10px !important;
    }

    .aot_inner {

        /*** HOME SIDEBAR ***/
        border: 1px solid var(--border-color) !important;
    }

    /* TORRENTS */

    /** DESCRIPTION : change the elements of the torrents page **/
    #torrents #content {
        margin-top: -75px !important;
    }

    #torrents2 #content {
        margin-top: -123px !important;
    }

    #browse_result_count {

        /*** TORRENTS RESULT COUNTER ***/
        display: none !important;
    }

    #torrents #browse_nav_top_left {

        /*** TORRENTS TITLE ***/
        display: none;
    }

    #torrents #browse_nav_tags,
    #torrents2 #browse_nav_tags {

        /*** TORRENTS TAGS WALL ***/
        display: none !important;
    }

    #torrents .group_cont,
    #torrents2 .group_cont {

        /*** TORRENTS BOXES ***/
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        padding: 20px !important;
    }

    #torrents .group_cont .group_img,
    #torrents2 .group_cont .group_img {

        /*** TORRENTS BOXES COVER ***/
        margin-right: 100px !important;
    }

    #torrents .group_cont .mainimg img,
    #torrents2 .group_cont .mainimg img {
        border: 1px solid var(--border-color) !important;
    }

    #torrents .cat,
    #torrents2 .cat {

        /*** TORRENTS BOXES CATEGORY ***/
        box-shadow: 0px 1px 2px rgba(0,0,0,.2) !important;
        width: 131px !important;
        padding: 5px 10px !important;
        line-height: 21px !important;
        overflow: hidden !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
    }

    #torrents .live_action_movie .cat,
    #torrents2 .live_action_movie .cat {
        width: 150px !important;
    }

    #torrents .live_action_tv_series .cat,
    #torrents2 .live_action_tv_series .cat {
        width: 180px !important;
    }

    #torrents .cat a,
    #torrents2 .cat a {
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        text-transform: uppercase !important;
        padding-top: 2px !important;
    }

    #torrents .group_cont .group_main,
    #torrents2 .group_cont .group_main {

        /*** TORRENTS BOXES MAIN ***/
        margin-left: 15% !important;
    }

    #torrents .group_cont .group_main .group_title *,
    #torrents2 .group_cont .group_main .group_title * {

        /*** TORRENTS BOXES MAIN TITLE ***/
        font-size: 18pt !important;
        font-stretch: condensed !important;
        font-weight: normal;
        line-height: 20pt !important;
    }

    #torrents .group_cont .group_main .group_title,
    #torrents2 .group_cont .group_main .group_title {
        color: transparent;
    }

    #torrents .group_cont .group_main .group_title strong,
    #torrents2 .group_cont .group_main .group_title strong {
        color: var(--primary-color) !important;
    }

    #torrents .group_cont .group_main .tags_sm,
    #torrents2 .group_cont .group_main .tags_sm {

        /*** TORRENTS BOXES MAIN TAGS ***/
        border-bottom: 1px dotted #BEBEBE !important;
        padding-bottom: 15px !important;
        margin-top: -10px !important;
        margin-bottom: 20px !important;
    }

    #torrents .group_cont .group_main .tags_sm .tags,
    #torrents2 .group_cont .group_main .tags_sm .tags {
        color: transparent !important;
    }

    #torrents .group_cont .group_main .tags a:not([href="?tags="]),
